) ENGINE=MyISAM DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `support_departments`;
CREATE TABLE `support_departments` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(19) NOT NULL,
  `email` varchar(99) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `tickets`;
CREATE TABLE `tickets` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ticket_id` varchar(21) NOT NULL,
  `title` text NOT NULL,
  `booking_ref` varchar(21) NOT NULL,
  `user_id` int(11) NOT NULL,
  `company_admin_id` int(11) NOT NULL,
  `name` varchar(21) NOT NULL,
  `email` varchar(99) NOT NULL,
  `contact` varchar(19) NOT NULL,
  `department` int(11) NOT NULL,
  `urgency` enum('High','Medium','Low') NOT NULL,
  `date` datetime NOT NULL,
  `assign_to` int(11) NOT NULL,
  `assign_date` datetime NOT NULL,
  `status` enum('Open','Closed') NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `ticket_chats`;
CREATE TABLE `ticket_chats` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ticket_id` varchar(21) NOT NULL,
  `message` text NOT NULL,
  `attachment` text NOT NULL,
  `clientunread` enum('Yes','No') NOT NULL,
  `adminunread` enum('Yes','No') NOT NULL,
  `Companyread` enum('Yes','No') NOT NULL DEFAULT 'No',
  `replyingtime` datetime NOT NULL,
  `replyingadmin` int(11) NOT NULL,
  `reply_by` enum('Client','Admin','Company') NOT NULL DEFAULT 'Client',
  `reply_to` enum('Client','All','Company') NOT NULL DEFAULT 'All',
  `hold` enum('Yes','No') NOT NULL DEFAULT 'Yes',
  `users_beep` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `ticket_notes`;
CREATE TABLE `ticket_notes` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ticket_id` int(11) NOT NULL,
  `note` text NOT NULL,
  `admin_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `email` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `password` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `remember_token` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `active` tinyint(4) DEFAULT '0',
  `support_department` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `users_menus`;
CREATE TABLE `users_menus` (
