INSERT INTO `faqs` (`id`, `title`, `content`, `added_by`, `type`, `added_on`, `updated_on`, `status`, `removed`) VALUES
(11,	'HOW CAN I CHANGE MY BOOKING DETAILS?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Yes, you can change dates, timings, flight number, vehicle details during our office hours by calling us on 02070580500 &nbsp;or email us at </span><a href=\"mailto:bookings@parkingzone.co.uk\" style=\"background-color: rgb(255, 255, 255);\">bookings@parkingzone.co.uk</a><span lang=\"EN-IN\">, where our customer services team will be happy to assist you.</span></p>',	1,	'Parking',	'2017-03-16 14:23:30',	'2017-03-16 14:23:30',	'Yes',	'No'),
(12,	'WHAT TIME SHOULD I ARRIVE AT THE CAR PARK OR MEETING AREA?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Typically between 2-3 hours before your flight departure. More specifically, for domestic flights we suggest 2 hours and for international flights 3 hours.</span></p>',	1,	'Parking',	'2017-03-16 14:24:17',	'2017-03-16 14:24:17',	'Yes',	'No'),
(13,	'WHAT HAPPENS IF MY FLIGHT IS DELAYED / EARLY?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Our car parks keep track of any changes in the flight schedule; you don&rsquo;t need to worry if you arrive before midnight. However, If you arrive after midnight, there is a &pound;10 charge, payable to the car park directly. If you are arriving earlier than expected, please contact the car park to arrange your vehicle to be ready for collection. Any part-time stays at car parking will be charged the full booking price.</span></p>',	1,	'Parking',	'2017-03-16 14:24:37',	'2017-03-16 14:24:37',	'Yes',	'No'),
(10,	'CAN I BOOK OR DISCUSS MY BOOKING OVER THE PHONE?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Yes, you can, our customer services team is glad to answer any queries and make a reservation for you.</span></p>',	1,	'Parking',	'2017-03-16 14:24:55',	'2017-03-16 14:24:55',	'Yes',	'No'),
(8,	'I HAVE LOST MY BOOKING CONFIRMATION?',	'<p><span style=\"font-size: 18px; line-height: 107%; font-family: Calibri, sans-serif; color: rgb(0, 32, 96);\" lang=\"EN-GB\">Log into your account where you can download a copy of your booking confirmation or email us at </span><span style=\"line-height: 107%;\" lang=\"EN-GB\"><a href=\"mailto:bookings@parkingzone.co.uk\" style=\"\"><font color=\"#002060\" face=\"Calibri, sans-serif\"><span style=\"font-size: 11pt;\"></span></font></a><font color=\"#002060\" face=\"Calibri, sans-serif\"><a href=\"mailto:bookings@parkingzone.co.uk\"><span style=\"font-size: 18px;\">bookings@p</span></a></font></span><font color=\"#002060\" face=\"Calibri, sans-serif\"><span style=\"font-size: 18px;\"><a href=\"mailto:bookings@parkingzone.co.uk\">arkingzone</a></span><span style=\"font-size: 18px;\"><a href=\"mailto:bookings@parkingzone.co.uk\">.co.uk</a>.</span></font></p>',	1,	'Parking',	'2017-03-08 07:25:31',	'2017-03-08 07:25:31',	'Yes',	'No'),
(9,	'I CANNOT PRINT MY BOOKING CONFIRMATION?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Please secure a note of your booking reference, the telephone number and directions to the car park. On the contrary, if you are a smartphone user, you can show the booking confirmation to the car park representative on your arrival.</span></p>',	1,	'Parking',	'2017-03-16 14:25:38',	'2017-03-16 14:25:38',	'Yes',	'No'),
(14,	'WHICH NUMBER SHOULD I CALL TO INFORM, I AM 15 MINUTES AWAY?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Kindly check your booking confirmation for arrival procedure and the car park helpline for any further queries in this regard. Parkingzone is a booking agent and will not be meeting you at the airport meeting point.&nbsp;&nbsp;</span></p>',	1,	'Parking',	'2017-03-16 14:25:58',	'2017-03-16 14:25:58',	'Yes',	'No'),
(34,	'WHY IS A WEEK COUNTED AS 8 DAYS AND TWO WEEKS AS 15 DAYS?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">This is based on how the car parks calculate their prices. Our system calculates your departure or arrival date as a whole day.</span></p>',	1,	'Parking',	'2017-03-16 14:26:52',	'2017-03-16 14:26:52',	'Yes',	'No'),
(15,	'WHY A WEEK CALCULATED AS 8 DAYS AND TWO WEEKS AS 15 DAYS?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">This is based on how the car parks calculate their prices. Our system calculates your departure or arrival date as a whole day.</span></p>',	1,	'Parking',	'2017-03-08 07:33:10',	'2017-03-08 07:33:10',	'Yes',	'No'),
(16,	'HOW DO I MAKE A COMPLAINT?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Our team is focused towards entertaining customer complaints. You can email us at </span><a href=\"mailto:customerservices@parkingzone.co.uk\">customerservices@parkingzone.co.uk</a><span lang=\"EN-IN\">, and you will receive a response within seven working days. Parkingzone is a booking agent; we will not be responsible for any complaints regarding damages or car park service itself. We always try to emphasize our car parks to provide the best services to our customers and minimize the negligence. We suggest to all our customers, make sure to check your vehicle before leaving the car park or upon collection at meeting area.</span></p>',	1,	'Parking',	'2017-03-16 14:27:16',	'2017-03-16 14:27:16',	'Yes',	'No'),
(17,	'I HAVE LOST MY BOOKING CONFIRMATION?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Log into your account where you can download a copy of your booking confirmation or email us at </span><a href=\"mailto:bookings@parkingzone.co.uk\">bookings@parkingzone.co.uk</a><span lang=\"EN-IN\"> .</span></p>',	1,	'Hotels',	'2017-03-16 14:28:00',	'2017-03-16 14:28:00',	'Yes',	'No'),
(18,	'I CANNOT  PRINT MY BOOKING CONFIRMATION?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Please secure a note of your booking reference, telephone number and directions to the Hotel. Or if you are a smartphone user, you can show the booking confirmation at the Hotel reception on your arrival.</span></p>',	1,	'Hotels',	'2017-03-16 14:28:18',	'2017-03-16 14:28:18',	'Yes',	'No'),
(19,	'CAN I BOOK OR DISCUSS MY BOOKING OVER THE PHONE?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Yes, you can. Our customer services team is glad to answer any queries and make a reservation for you.</span></p>',	1,	'Hotels',	'2017-03-16 14:28:36',	'2017-03-16 14:28:36',	'Yes',	'No'),
(20,	'HOW CAN I CHANGE MY BOOKING DETAILS?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Please contact our customer services team on 02070580500 or email us at </span><a href=\"mailto:bookings@parkingzone.co.uk\">bookings@parkingzone.co.uk</a><span lang=\"EN-IN\"> for any changes required.</span></p>',	1,	'Hotels',	'2017-03-16 14:28:52',	'2017-03-16 14:28:52',	'Yes',	'No'),
(21,	'IS THERE ANY CHARGE FOR CANCELLATION OF MY HOTEL BOOKING?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Cancellation fees are based on per room at &pound;15. Few discounted rooms cannot be canceled or amended; please check the cancellation details before booking.</span></p>',	1,	'Hotels',	'2017-03-16 14:29:10',	'2017-03-16 14:29:10',	'Yes',	'No'),
(22,	'WHAT DAY DO I BOOK FOR IF I AM ARRIVING EARLY HOURS?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">You should book your room a night before, for instance if you are arriving at 4 am on 2nd  march, the night of 1st march should be booked for overnight stay.</span></p>',	1,	'Hotels',	'2017-03-08 07:36:04',	'2017-03-08 07:36:04',	'Yes',	'No'),
(23,	'CAN I SMOKE IN MY HOTEL ROOM?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Availability of smoking rooms cannot be guaranteed; some hotels do offer the service, while others do not have this facility.&nbsp;</span></p>',	1,	'Hotels',	'2017-03-16 14:29:58',	'2017-03-16 14:29:58',	'Yes',	'No'),
(24,	'PETS ALLOWED IN THE HOTEL?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Some hotels do allow pets if you are not sure please call us 02070580500 or email us <a href=\"mailto:customerservice@parkingzone.co.uk\">customerservice@parkingzone.co.uk</a> .</span></p>',	1,	'Hotels',	'2017-03-16 14:30:16',	'2017-03-16 14:30:16',	'Yes',	'No'),
(25,	'I HAVE LOST MY BOOKING CONFIRMATION?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Log into your account where you can download a copy of your booking confirmation or email us at </span><a href=\"mailto:bookings@parkingzone.co.uk\">bookings@parkingzone.co.uk</a><span lang=\"EN-IN\"> . Please ensure you have your lounge pass printed at the time of your arrival at the lounge.</span></p>',	1,	'Lounges',	'2017-03-16 14:30:48',	'2017-03-16 14:30:48',	'Yes',	'No'),
(26,	'CAN I BOOK OR DISCUSS MY BOOKING OVER THE PHONE?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Yes, you can. Our customer services team are glad to answer any queries and make a reservation for you.</span></p>',	1,	'Lounges',	'2017-03-16 14:31:08',	'2017-03-16 14:31:08',	'Yes',	'No'),
(36,	'HOW LONG CAN I USE THE LOUNGE FACILITIES?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">You can stay up to three hours before your flight. Please check the timings for more info or further details, while booking.&nbsp;</span></p>',	1,	'Lounges',	'2017-03-16 14:31:33',	'2017-03-16 14:31:33',	'Yes',	'No'),
(27,	'HOW LONG I CAN USE THE LOUNGE FACILITIES?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">You can stay up to 3 hours before your flight. Please check the timings on more info for further details while booking.&nbsp;</span></p>',	1,	'Lounges',	'2017-03-08 07:37:52',	'2017-03-08 07:37:52',	'Yes',	'No'),
(28,	'HOW DO I MAKE GROUP BOOKINGS?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Please call our customer services team if you are booking for more than seven people as pre-authorization is required for group bookings.</span></p>',	1,	'Lounges',	'2017-03-16 14:31:56',	'2017-03-16 14:31:56',	'Yes',	'No'),
(29,	'IS THERE A DRESS CODE FOR LOUNGES?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">All Airport Lounges have a casual dress code; please ensure you have the right dress code otherwise you might be refused to enter.</span></p>',	1,	'Lounges',	'2017-03-16 14:32:14',	'2017-03-16 14:32:14',	'Yes',	'No'),
(30,	'IS THERE A CHARGE FOR CANCELLATION?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">Any cancellation made 48 hours before booking date will be charged 10% as administration costs. In the event of fewer than 48 hours&rsquo; notice, no refund shall be granted.&nbsp;</span></p>',	1,	'Lounges',	'2017-03-16 14:32:32',	'2017-03-16 14:32:32',	'Yes',	'No'),
(31,	'CAN I SMOKE AT LOUNGE?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">We apologize for any inconvenience, as all the Airport lounges are a non-smoking area.&nbsp;</span></p>',	1,	'Lounges',	'2017-03-16 14:32:48',	'2017-03-16 14:32:48',	'Yes',	'No'),
(32,	'WHERE CAN I FIND MY AIRPORT LOUNGE?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">On your booking confirmation, clear direction to the lounge will be given. Directions can also be found at \'More Info Section.\'</span></p>',	1,	'Lounges',	'2017-03-16 14:33:10',	'2017-03-16 14:33:10',	'Yes',	'No'),
(33,	'ARE THE LOUNGES SUITABLE FOR DISABLED?',	'<p class=\"MsoNormal\"><span lang=\"EN-IN\">All our airport lounges have facilities for disabled people. Further information is available on \'More Info Link.\'</span></p>',	191,	'Lounges',	'2017-09-06 13:23:57',	'2017-09-06 13:23:57',	'Yes',	'No');
